let myBurger=document.querySelector(".burger-menu"),myNav=document.querySelector(".nav-link"),bgMenu=document.querySelector(".menu-bg"),link=document.querySelectorAll(".nav__link");function hasClick(){myNav.classList.toggle("active"),bgMenu.classList.toggle("active")}for(myBurger.addEventListener("click",hasClick),bgMenu.addEventListener("click",hasClick),i=0;i<link.length;i++)link[i].addEventListener("click",hasClick);let Naviguation=document.querySelector(".nav"),sticky=10;document.addEventListener("scroll",()=>{window.pageYOffset>sticky?Naviguation.classList.add("sticky"):Naviguation.classList.remove("sticky")});